public function getOrderCredits($order_id) {
  $query = $this->db->query("SELECT * FROM " . DB_PREFIX . "credit WHERE order_id = '" . (int)$order_id . "'");

  return $query->rows;
}

public function getOrderCreditByCreditId($credit_id) {
